typedef long long ll;
class Solution {
public:
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        sort(flowers.begin(), flowers.end());
        long long ans;
        // Will use this vector to store all gardens with flowers less than target
        vector<long long int> p;
        long long int fullc = 0;
        for(int i=0;i<flowers.size();i++){
            if(flowers[i] >= target){
                fullc += 1;
            }else{
                p.push_back(flowers[i]);
            }
        }
        // Initalizing ans with base value
        ans = fullc * full;
        if(p.size() == 0)
            return ans;
        // Defining and creating prefix array which will be storing the number of flowers required for makeing all lower flowers
        // equal to the current number
        vector<long long int> pref(p.size(), 0);
        for(int i=1;i<p.size();i++){
            pref[i] = (p[i] - p[i-1]) * i + pref[i-1];
        }
        long long int val = 0;
        // Considering all partial and finding best possible ans
        long long int index = upper_bound(pref.begin(), pref.end(), newFlowers) - pref.begin();
        index -= 1;

        long long int count = (newFlowers - pref[index]) / (index + 1);
        if(count + p[index] >= target){
            val = 1ll * partial * (target - 1);
        }else{
            val = (p[index] + count) * partial;
        }
        // Updating ans for base case
        ans = max(ans, val + ans);

        long long int i = p.size() - 1;

        // Iterating and adding garden to full group and calculating ans
        while(i >= 0){
            long long int currVal = 0;
            // Flowers required for current garden to reach target
            long long int toFull = target - p[i];

            // If enough flowers are not there then break the loop and exit (ans is already received)
            if(toFull >= newFlowers)
                break;
            fullc += 1;
            newFlowers -= toFull;
            // When all flowers are full
            if(i == 0){
                // updating ans if all garden are part of full group 
                ans = max(ans, fullc * full);
                break;
            }
            // Binary search to find the best possible number closest to the remaining flowers
            // So that we can calculate the max beauty of the partial garden 
            index = upper_bound(pref.begin(), pref.begin() + i, newFlowers) - pref.begin();
            index -= 1;

            // Calculating partila beauty for the current iteration
            long long int count = (newFlowers - pref[index]) / (index + 1);
            if(count + p[index] >= target){
                currVal = 1ll * partial * (target - 1);
            }else{
                currVal = 1ll * (p[index] + count) * partial;
            }

            // keep updating ans
            ans = max(ans, fullc * full + currVal);
            i-=1;
        }


        return ans;
    }
};
