        if(l<nums.size()) ans+=(long long)partial*find(nums,t,n,l);
        for(long long i = l+1;i<=h;++i){
            long long p = 0;
            if(nums.size()>i) p=find(nums,t,arr[i-l-1],i); // Get the maximized minimum no. of flowers if `i` gardens are completed and arr[i-l-1] flowers are left
            p = i*full +p*partial;
            ans = max(ans,p);
        }
        return ans;
    }
};
