class Solution {
    public long maximumBeauty(int[] flowers, long newFlowers, int target, int full, int partial) {
        /*
           Sort the array,
           Complete the number of flowers from the last and check how much maximum minimum flowers in partial gardens we can get
           from the remaining flowers using binary search
        */

        long ans = 0;

        Arrays.sort(flowers);
        int numFlowers = flowers.length;
        long[] cost = new long[numFlowers]; // Cost to get minimum flowers as the current flower till the current index 

        int isComplete = flowers[0] >= target ? 1 : 0;
        for(int i=1; i<numFlowers; i++){
            cost[i] = cost[i-1] + (flowers[i] - flowers[i-1])*i;
            if(flowers[i] >= target) isComplete++;
        }

        if(isComplete == flowers.length) return (long)numFlowers*full; // If all gardens are full

        long costToCompleteTillNow = 0;
        for(int i=numFlowers; i>=0; i--){
            if(i < numFlowers){
                costToCompleteTillNow += Math.max(0, target - flowers[i]);
                if(costToCompleteTillNow > newFlowers) break;
                if(i == 0){
                    ans = Math.max(ans, (numFlowers)*(long)full);
                    break;
                }   
            }
            // Find the index till which we can have partial garden from the remaining flowers
            long remFlower = newFlowers - costToCompleteTillNow;
            int maxPartialIndex = findByBinarySearch(remFlower, cost, 0, i-1);

            if(maxPartialIndex < 0) continue;

            long remFlowerAfterDist = remFlower - cost[maxPartialIndex]; // Remaining flowers after paying the cost to make all equal
            long flowerToAdd = remFlowerAfterDist/(maxPartialIndex + 1); // Equally distribute the remaining flowers

            ans = Math.max(ans, (((long)full*(numFlowers - i))  + ((long)partial)*(Math.min(target - 1, flowers[maxPartialIndex] + flowerToAdd))  ));
        }

        return ans;
    }

    private int findByBinarySearch(long key, long[] arr, int l, int h){  
        if(l > h || arr[l] > key) return -1;

        int ans = 0;
        h += 1;
        while(l < h){
            int m = l + (h-l)/2;
            if(arr[m] <= key){
                ans = m;
                l = m+1;
            }else h = m;
        }

        return ans;
    }
}
