                partialCount = (long) flowers[indexForPartialGardens] + (long) ((remainingFlowersCount - fillUpPartialGarden[indexForPartialGardens])/(long)(indexForPartialGardens+1));

                //partialCount is capped at target-1
                partialCount = Math.min(partialCount, target-1); 

            }
            long temp = (long) (countOfFullGardens + filledGardens) * (long) full +  (long) partialCount * (long) partial;
            ans = Math.max(temp, ans);
        }

        return ans;
    }

    // Expects sorted array of flowers and returns count of already complete gardens
    private int getCountOfCompletedGardens(int[] flowers, int target) {
        int countOfFullGardens = 0;
        for(int i = flowers.length - 1; i >= 0; i--) {
            if(flowers[i] < target)
                break;
            countOfFullGardens++;
        }
        return  countOfFullGardens;
    }


    private long[] getCountNeededForPartialGarden(int[] flowers, int updatedLength) {
        long[] fillUpPartialGarden = new long[updatedLength];
        for(int i = 1; i < updatedLength; i++) {
            // At i, the i gardens preceding it (0 indexed) already have flowers[i-1] flowers
            fillUpPartialGarden[i] = (long)(flowers[i] - flowers[i-1]) *(long)i + (long)fillUpPartialGarden[i-1];

        }
        return fillUpPartialGarden;
    }


  // Returns an array where value at index i denotes the number of flowers needed so that all the length - i + 1 elements from the index onwards have completed gardens
    private long[] getCountNeededForCompleteGarden(int[] flowers, int updatedLength, long target) {
        long fillUpCompletedGarden[] = new long[updatedLength];

        fillUpCompletedGarden[updatedLength-1] = target - flowers[updatedLength-1];
        for(int i = updatedLength - 2; i >= 0; i--) {
            fillUpCompletedGarden[i] = (long)fillUpCompletedGarden[i+1] + (long)(target - flowers[i]); 
        }
        return fillUpCompletedGarden;
    }

    // Returns the index such that nums[index] is the maximum value less than value
     private int binarySearch(long[] nums, int length, long value) {
        int low = 0;
        int high = length-1;
        int ans = low;

        while (low <= high) {
            int mid = low + (high-low)/2;
            if ((long) nums[mid] ==  (long) value) 
                return mid;
            else if ((long) nums[mid] < (long) value) {
                ans = mid;
                low = mid + 1;
            }
            else high = mid - 1;
        }

        return ans;
    }
}```

