class Solution {
public:
    #define ll unsigned long long int
    int possible(ll srt,ll end,ll left,ll target,vector<int> &flowers,vector<ll> &prefix){
        if(srt>end)return 0;

        ll n = flowers.size();
        ll l = flowers[end];          // min value in srt...end and minimum can't be decreased than min value 
        ll r = target-1;     // maximum value. we want to change number to atmost target-1. if we make target it becomes complete garden,
        ll ans = l;
        ll idx;
        while(l<=r){
            ll mid = l+(r-l)/2;

			// Finding index in flowers where flowers[idx] > mid
             auto it = upper_bound(flowers.rbegin(),flowers.rend(),mid);

            if(it==flowers.rend()){
                idx = srt-1;
            }else{
                idx = n-1-(it-flowers.rbegin());
            }

            // from idx+1...end all numbers will be smaller or equal to mid

			// This is the amount required to make all numbers in range idx+1..end to mid
            long long int change = mid*(end-idx)-(prefix[end+1]-prefix[idx+1]);

			// If change is less than flowers left we can increase every value which is less than mid, to mid and hence minimum in array becomes mid.
            if(change<=left){
                l = mid+1;
                ans = mid;
            }else{
                r = mid-1;
            }
        }
        return ans;
    }

    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        ll n = flowers.size();
        sort(flowers.begin(),flowers.end(),greater<int>());
        vector<ll> prefix(n+1,0);

        for(int i=1;i<=n;i++){
            prefix[i] = prefix[i-1]+flowers[i-1];
        }


        long long int k = newFlowers;
        ll score = 0;
        ll mul = 1;


        for(int i=0;i<n && k>=0;i++){

		// if garden is already full,we dont need to anything,just add up score
            if(flowers[i]>=target){
                score+=full;
                continue;
            }

            // Finding  maximum "minimum value" in leftover gardens by using the leftover flowers we have.
            ll mipossible = possible(i,n-1,k,target,flowers,prefix);

			score = max(score,mul*i*full+mipossible*partial);

            // making garden[i] complete by using up flowers we have
			k-=(target-flowers[i]);

			// change flowers[i] to target as we increased flowers in i th garden
            flowers[i] = target;
        }

        if(k>=0)score = max(score,mul*n*full);

        return score;
    }
};
```

*Time Complexity:   O(Nlog(target)log(N))*

