            // tasks = [4,4,4,4,4]
            else if ( it.second>3 ){
                int rem = it.second%3;
                // multiple of 3 
                rounds += it.second/3;
                // multiple of 2 
                if( rem%2 == 0 ) rounds += rem/2;
                // For tasks = [4,4,4,4], rem == 1;
                else rounds += 1;
            }
        }

        return rounds;
    }
};
```

