    def composedBy(self, num):
        final_step = -1
        tmp_step = 0

        while num > 0:
            if num % 3 == 0:
                final_step = num // 3 + tmp_step
                return final_step

            else:
                num -= 2
                tmp_step += 1

        if num == 0:
            return tmp_step
        else:
            return final_step

