class Solution {
public:
    int minimumRounds(vector<int>& tasks) {
        int ans = 0;
        unordered_map<int,int>mp;
        for(int x: tasks){
            mp[x]++;
        }

        for(auto x: mp){
            //if the occurence of any difficulty is only 1 then there is no chance that the task can be done so we will simply return -1
            if(x.second == 1){
                return -1;
            }

            else{
                //as we are moving greedily so we will try to minimize the rounds so firstly we will prefer to complete the tasks in the group of three
                int rem = x.second%3;
                int usedrounds = x.second/3;
                ans+=usedrounds;

                //when we divide a number by 3  then the possible remainders can be 0 1 or 2
                if(rem == 1){
                    //for removing the last entry of the 3 so that remainder will now be 1 + 3
                    ans-=1;

                    //if now the remainder is 4 then we can do it in the group of 2 2 
                    ans+=2;
                }

                else if(rem == 2){
                    //if the remainder is 2 then that level can be completely in one round by doing 2 tasks
                    ans+=1;
                }
            }
        }

        return ans;
    }
};
