use std::collections::HashMap;

impl Solution {
    pub fn minimum_rounds(tasks: Vec<i32>) -> i32 {
        let mut map = HashMap::new();
        let mut ans = 0;

        for task in tasks {
            let val = map.get_mut(&task);

            if let Some(v) = val {
                *v += 1;
            } else {
                map.insert(task, 1);
            }
        }

        for count in map.into_values() {
            if count == 1 {
                return -1;
            }
            ans += (count + 2) / 3;
        }

        ans
    }
}

