 */
var minimumRounds = function(tasks) {
    let map = {} // freq map
    let steps = 0 // steps result

    for (let i of tasks) {
        if (map[i] !== undefined) {
            map[i]++
        } else {
            map[i] = 1
        }
    }

    for (let i in map) {
        if (map[i] === 1) return -1
        else if (map[i] === 2) steps++
        else if (map[i] > 2) {
            if (map[i] % 3 > 0) steps += Math.trunc(map[i] / 3) + 1
            else steps += map[i] / 3
        }
    }
    return steps
};
