class Solution {
public:
    string largestGoodInteger(string num) 
    {
        int count=1; //count triplet
        string res; //store result triplet
        int max=INT_MIN; //helps in store max triplet

        //iterate over string
        for(int i=0; i<num.size()-1; i++)
        {
            //if current char is equal to the next char, increase the count by 1
            if(num[i]==num[i+1])
                count++;

            //if current char is not equal to the next char, re-initialize the count by 1
            else
                count=1;

            //if count is equal to 3 and num[i] is greater than the max, store the triplet into the result
            if(count==3 && (num[i]-'0') > max)
            {
                res="";
                while(count--) //store triplet
                    res += num[i];
                max = (num[i]-'0');
                count=1; //re-initialize count by 1
            }
        }
        return res; 
    }
};
```

