const largestGoodInteger = function(num) {
    let a = 0
    let maxGood = ''
    while (a < num.length - 2) {
        let b = a + 1
        let c = b + 1    
        if (num[a] === num[b] && num[b] === num[c]) {
            if ((num[a] + num[b] + num[c]) > maxGood) {
                maxGood = num[a].repeat(3)
            }
        }
        a++
    }
    return maxGood
};
