class Solution {
public:
    string largestGoodInteger(string num) {
        int cnt=1;
        string x="";
        string s="";
        for(int i=0;i<num.size()-1;i++){
            if(num[i]==num[i+1]){
                cnt++;
                if(cnt==3){
                    s+=num[i];
                    s+=num[i];
                    s+=num[i];
                    if(x.size()==0){
                        x=s;
                    }
                    else if(x[0]<s[0]){
                        x=s;
                    }
                }
                s="";
            }
            else{
                cnt=1;
            }
        }
        return x;
    }
};
