const largestGoodInteger = function(num) {

    let max = 0

    for (let i = 0; i < num.length; i++) {

        if (num[i - 1] === num[i]  && num[i - 2] === num[i]) {
            let int  = parseInt(num[i])
            max = Math.max(max, int)
        }

    }

    if (max === 0) {
        if (num.includes('000')) {
            max = '0'
        } else {
            max = ''
        }
    }

    max = max + ''

    return max.repeat(3)

}
```

