public class Solution {
    public string LargestGoodInteger(string num) {
        char lastChar = 'a';
        int count = 1;
        string highest = string.Empty;

        foreach (char c in num)
        {
            if (lastChar.Equals(c))
            {
                count++;

                if (count == 3)
                {
                    if (highest != string.Empty)
                    {
                        if (int.TryParse(c.ToString(), out int current) && int.TryParse(highest, out int highestInt))
                        {
                            if (current > highestInt)
                            {
                                highest = current.ToString();
                            }
                        }
                    }
                    else
                    {
                        highest = c.ToString();
                    }

                    count = 1;
                }
            }
            else
            {
                count = 1;
            }

            lastChar = c;
        }

        return highest + highest + highest;
    }
}
```

