```c++
struct Node {
    int left_, right_, cnt_;
    Node *lc_, *rc_;

    Node (int left, int right, int cnt = 0) {
        left_ = left;
        right_ = right;
        cnt_ = cnt;
        lc_ = NULL;
        rc_ = NULL;
    }

    int cnt() {
        return cnt_;
    }

    int size() {
        return right_ - left_ + 1;
    }

    int mid() {
        return (left_ + right_) >> 1;
    }

    Node *lc() {
        if (lc_ == NULL) {
            lc_ = new Node(left_, mid());
        }
        return lc_;
    }

    Node *rc() {
        if (rc_ == NULL) {
            rc_ = new Node(mid() + 1, right_);
        }
        return rc_;
    }

    void insert(int left, int right) {
        if (size() == cnt()) {
            return;
        }
        if (left <= left_ && right >= right_) {
            cnt_ = size();
            return;
        }
        if (left <= mid()) {
