	this.intervals[second].start = this.intervals[first].start
	copy(this.intervals[first:], this.intervals[second:])
	this.intervals = this.intervals[:len(this.intervals)-(second-first)]
}

func (this *CountIntervals) Count() int {
	return this.count
}
