class CountIntervals {
private:
    set<pair<int, int>> interval;
    int ans;
public:
    CountIntervals() {
        interval = * new set<pair<int, int>>();
        ans = 0;
    }

    void add(int left, int right) {
        if (interval.size() == 0) {
            interval.insert(make_pair(left, right));
            ans += right - left + 1;
        } else {
            pair<int, int> num = make_pair(left, right);
            int sub = 0;

            if (interval.lower_bound(num) == interval.begin()) {
                while (interval.begin()->second <= right) {
                    sub += interval.begin()->second - interval.begin()->first + 1;
                    interval.erase(interval.begin());

                    if (interval.empty())
                        break;
                }

                if (!interval.empty() && interval.begin()->first <= right) {
                    sub += right - interval.begin()->first + 1;
                    num.second = interval.begin()->second;
                    interval.erase(interval.begin());
                }
            } else {
                if (prev(interval.lower_bound(num))->second >= left) {
                    int ll = prev(interval.lower_bound(num))->first;

                    if (prev(interval.lower_bound(num))->second >= right) {
                        sub += right - left + 1;
                        num.second = prev(interval.lower_bound(num))->second;
                        interval.erase(prev(interval.lower_bound(num)));
                    } else {
                        sub += prev(interval.lower_bound(num))->second - left + 1;
                        interval.erase(prev(interval.lower_bound(num)));
                    }

                    num.first = ll;
                }

                while (interval.lower_bound(num) != interval.end() && right >= interval.lower_bound(num)->first) {
                    if (right > interval.lower_bound(num)->second)
                        sub += interval.lower_bound(num)->second - interval.lower_bound(num)->first + 1;
                    else {
                        sub += right - interval.lower_bound(num)->first + 1;
                        num.second = interval.lower_bound(num)->second;
                    }

                    interval.erase(interval.lower_bound(num));
                }
            }

            interval.insert(num);
            ans += right - left + 1 - sub;
        }
    }

    int count() {
        return ans;
    }
};
