using pii = pair<int,int>;
const int inf = 1e9+7;

class CountIntervals {
public:
  set<pii> s;
  int cnt = 0 ;
  CountIntervals() {
    // Give some boundary for safety. 
    s.insert({-1,-1});   
    s.insert({inf,inf}); 
  }

  // Note that each interval inserted by a single call of add(int left, int right) can be removed at most once. 
  // So the overall complexity is O(nlogn).
  void add(int left, int right) {
    auto it = s.lower_bound({left,0});  // look for an interval with smallest l s.t. left <= l
    int l,r;  // the left end and the right end of to-be-inserted interval {l,r}
    l = left; 
    r = right;
    if (it!=s.begin()) { // if there is an interval left side of it
      auto temp = prev(it);
      if ((*temp).second >= left) { // check if the cur.r >= left 
        l = (*temp).first;          // l should be updated 
        it = temp;
      }
    }
    while (it != s.end()) {  // traverse through the set of intervals
      if ((*it).second <= right) {  // the entire interval is included in [left,right]
        auto [ll,rr] = *it;
        cnt -= rr-ll+1;             // update cnt
        auto temp = next(it);
        s.erase(it);                // remove current interval represented by it
        it = temp;
      }  
      else {                         // the entire interval is not included in [left,right]
        if ((*it).first > right) {   // current interval represented by it does not overlap with to-be-inserted interval {l,r}
          break;
        }
        else {                       // current interval represented by it overlaps with to-be-inserted interval, so we update r
          r = ((*it).second);
          auto [ll,rr] = *it;
          cnt -= rr-ll+1;           // update cnt
          s.erase(it);              // remove current interval represented by it
          break;
        }
      }
    }
    s.insert({l,r});          // finally insert what we have prepared so far.
    cnt += r-l+1;             // update cnt
  }

  int count() {
      return cnt;
  }
};

