class CountIntervals {
public:   
    void add(int left, int right) {
        const auto [lo, hi] = _s.equal_range(std::make_pair(left, ++right));
        if (lo != hi) {
            for (auto it = lo; it != hi; ++it)
                _count -= it->second - it->first;
            left = std::min(left, lo->first);
            right = std::max(right, std::prev(hi)->second);
            _s.erase(lo, hi);
        }
        _s.emplace(left, right);
        _count += right - left;
