# Code
```
class CountIntervals {
public:
    multiset<pair<int, int>> setIntervals;
    int numIntegers = 0;

    CountIntervals() {

    }

    void add(int left, int right) {
        setIntervals.insert({left, right});
        numIntegers += right - left + 1;

        auto it = setIntervals.lower_bound({left, right});
        while (next(it) != setIntervals.end() && next(it)->first <= right) {
            it = mergeTwoIntervals(it, next(it));
        }

        while (it != setIntervals.begin() && prev(it)->second >= left) {
            it = mergeTwoIntervals(prev(it), it);
        }
    }

    multiset<pair<int, int>>::iterator mergeTwoIntervals(multiset<pair<int, int>>::iterator it1, multiset<pair<int, int>>::iterator it2) {
        int rightRangeDuplicate = min(it1->second, it2->second);
        int leftRangeDuplicate = max(it1->first, it2->first);
        int newRightRange = max(it1->second, it2->second);
        int newLeftRange = min(it1->first, it2->first);
        setIntervals.erase(it2);
        setIntervals.erase(it1);
        setIntervals.insert({newLeftRange, newRightRange});
        numIntegers -= rightRangeDuplicate - leftRangeDuplicate + 1;
        return setIntervals.lower_bound({newLeftRange, newRightRange});
    }

    int count() {
        return numIntegers;
    }
};

