class CountIntervals:

    def __init__(self):
        self.intervals = []

    def add(self, left: int, right: int) -> None:
        self.intervals.append([left, right])
        self.intervals.sort(key = lambda x:x[0])
        res = [self.intervals[0]]
        for u, v in self.intervals[1:]:
            if u <= res[-1][1]:
                res[-1][1] = max(res[-1][1], v)
            else:
                res.append([u, v])
        self.intervals = res.copy()

    def count(self) -> int:
        res = 0
        for u, v in self.intervals:
            res += (v-u+1)
        return res


