class CountIntervals {

    TreeMap<Integer, Integer> intervals;
    int sz = 0;

    public CountIntervals() {
        intervals = new TreeMap();
        sz = 0;
    }

    public void add(int left, int right) {

        int s = left, e = right; 
        Integer s1 = intervals.floorKey(left);

        if (s1 != null) {
            int e1 = intervals.get(s1);

            if (e1 >= e) return;
            if (e1 >= s) {
                sz -= (e1 - s1 + 1);
                s = Math.min(s1, s);
                intervals.remove(s1);
            }
        }

        while (intervals.floorKey(e) !=null && intervals.floorKey(e) >= s) {

            int s2 = intervals.floorKey(e);
            int e2 = intervals.get(s2);
            sz -= (e2 - s2 + 1);

            e = Math.max(e2, e);
            intervals.remove(s2);
        }

        sz += (e - s + 1);
        intervals.put(s, e);
    }

    public int count() {
        return sz;
    }
}

