class Node:
    def __init__(self, start, end, val=0):
        self.start = start
        self.end = end
        self.val = val
        self.left = self.right = None

class CountIntervals:

    def __init__(self):
        self.root = Node(1, 10**9)

    def add(self, start: int, end: int) -> None:
        self.update(self.root, start, end)

    def update(self, node, start, end):
#         to avoid updating what we already have with a lesser value...
        if node.val == node.end-node.start+1:
            return

        if node.start == start and node.end == end:
            node.val = node.end - node.start +1
            return

        mid = (node.start + node.end) // 2

        if not node.left:
            node.left = Node(node.start, mid, 0)
        if not node.right:
            node.right = Node(mid+1, node.end, 0)

        if end <= mid:
            self.update(node.left, start, end)
        elif start >= mid + 1:
            self.update(node.right, start, end)
        else:
            self.update(node.left, start, mid)
            self.update(node.right, mid+1, end)

        node.val = node.left.val + node.right.val 
        return

    def count(self) -> int:
        return self.root.val


