class CountIntervals {
    TreeMap<Integer,Integer> map;
    int count=0;
    public CountIntervals() {
        map=new TreeMap<>();
    }

    public void add(int left, int right) {
        int l=left;
        int r=right;

        while(true){
            if(map.floorKey(r)==null || map.get(map.floorKey(r))<l)
                break;

            int prevLeft=map.floorKey(r);
            int prevRight=map.get(prevLeft);

            count-=(prevRight-prevLeft+1);
            map.remove(prevLeft);

            l=Math.min(l,prevLeft);
            r=Math.max(r,prevRight);
        }

        map.put(l,r);
        count+=(r-l+1);
    }

    public int count() {
        return count;
    }
}

