class CountIntervals {

    TreeMap<Integer, Integer> tree;
    int count;

    public CountIntervals() {
        this.tree = new TreeMap<>();
        this.count = 0;
    }

    public void add(int left, int right) {
	    // Keep removing lower entries as long as they extend current interval
        while (this.tree.floorKey(left) != null) {
            int fk = this.tree.floorKey(left);
			// The lower range is totally out of the current range
            if (this.tree.get(fk) < left) {
                break;
            }
            left = Math.min(left, fk);
            right = Math.max(right, this.tree.get(fk));
			// remove the previously counted values
            this.count -= (this.tree.get(fk) - fk + 1);
            this.tree.remove(fk);
        }
		// Keep removing higher entries as long as they extend current interval
        while (this.tree.floorKey(right) != null) {
            int fk = this.tree.floorKey(right);
			// the range is totally out of the current range
            if (this.tree.get(fk) < left) {
                break;
            }
            left = Math.min(left, fk);
            right = Math.max(right, this.tree.get(fk));
			// remove the previously counted values
            this.count -= (this.tree.get(fk) - fk + 1);
            this.tree.remove(fk);
        }
        this.count += (right - left + 1);
        this.tree.put(left, right);
    }

    public int count() {
        return this.count;
    }
}
