    def add(self, left: int, right: int) -> None:
        self._add(self.root, 1, 10**9, left, right)


    def _add(self, node, l, r, left, right):
        if not node:
            node = Node()
        if node.count == r-l+1:
            return node
        if l > right or r < left:
            return node
        if left <= l and r <= right:
            node.count = r-l+1
            # print(l, r, node.count)
            return node
        mid = (l+r) // 2
        node.left = self._add(node.left, l, mid, left, right)
        node.right = self._add(node.right, mid+1, r, left, right)
        node.count = node.left.count + node.right.count
        # print(l, r, node.count, node.left.count, node.right.count)
        return node


    def count(self) -> int:
        return self.root.count
