class CountIntervals {
    private TreeNode root = null;
    public CountIntervals() {
        root = new TreeNode();
    }
    public void add(int left, int right) {
		// Given number range is [1, 10^9]
        add(root, 1, (int)(1e9+1), left, right);
    }
    public int add(TreeNode node, int start, int end, int left, int right) {
		// If interval is out of the node range, don't need to update current node, return count directly.
        if (left > end || right < start) {
            return node.val;
        }
		// If the current node is fully covered, return count directly.
        if(end - start + 1 == node.val){
            return node.val;
        }
		// If is the leaf node, update the node count.
        if(start == left && end == right){
            return node.val = end - start + 1;
        }
		// Update the children tree nodes when needed(the interval added is between the node range)
        int mid = start + (end - start) / 2;
        if(node.left == null){
            node.left = new TreeNode();
        }
        if(node.right == null){
           node.right = new TreeNode(); 
        }
        int cnt = 0;
        cnt += add(node.left, start, mid, Math.max(start, left), Math.min(mid, right));
        cnt += add(node.right, mid+1, end, Math.max(mid+1, left), Math.min(end, right));
        return node.val = cnt;
    }
    public int count() {
        return root.val;
    }
}
