import java.util.*;
class CountIntervals {
    private NavigableMap<Integer, Integer> map;
    private int count;
    public CountIntervals() {
        map = new TreeMap<>();
        count = 0;
    }


    public void add(int left, int right) {
        int low = left;
        int high = right;
        while(map.floorKey(high) != null && map.get(map.floorKey(high)) >= low) {
            var prev = map.floorEntry(high);

            count -= (prev.getValue() - prev.getKey() + 1);
            map.remove(prev.getKey());
            low = Integer.min(prev.getKey(), low);
            high = Integer.max(prev.getValue(), high);
        }

        map.put(low, high);
        count += (high - low + 1);
        return;

    }


    public int count() {
        return count;
    }
}

