class CountIntervals {
    map<int, int> intervals;
    int total = 0;
public:
    void add(int left, int right) {
        const auto& [it, inserted] = intervals.emplace(left, right - left + 1);
        if (!inserted) {
            if (right - left + 1 < it->second)
                return;
            total -= it->second;
            it->second = right - left + 1; 
            total += it->second;
        }
        else
            total += it->second;

        for (auto next_it = next(it); next_it != end(intervals); next_it = intervals.erase(next_it)) {
            if (it->first + it->second >= next_it->first) {
                // 13 + 49 - 39 = sub 23
                const auto proposed_new_end = (next_it->first - it->first) + next_it->second;
                if (proposed_new_end > it->second) {
                    total -= it->second;
                    it->second = proposed_new_end;
                    total += it->second;
                }
            }
            else
                break;
            total -= next_it->second;
        }

        if (auto prev_it = make_reverse_iterator(it); prev_it != rend(intervals)) {
            if (prev_it->first + prev_it->second >= it->first) {
                total -= prev_it->second;
                prev_it->second = max(prev_it->second, (it->first - prev_it->first) + it->second);
                total += prev_it->second;
                total -= it->second;
                intervals.erase(it);
            }
        }
    }

    int count() {
        return total;
    }
};

