int i;

template<typename T=int>
struct SegTreeNode {
    int left, right;
    T val, lazy;
    SegTreeNode<T> *leftChild, *rightChild;
    T noOverlapVal = 0;

    SegTreeNode<int>* nodes;

    SegTreeNode() {

    }

    void init(int left, int right, SegTreeNode<int>* nodes) {
        this->nodes = nodes;
        this->left = left;
        this->right = right;
        this->val = 0;
        this->lazy = 0;
        this->leftChild = NULL;
        this->rightChild = NULL;
    }

    void extend() {
        if(not leftChild and left != right) {
            int mid = (left + right) / 2;
            leftChild = &(*(nodes+(i++)));
            leftChild->init(left, mid, nodes);
            rightChild = &(*(nodes+(i++)));
            rightChild->init(mid+1, right, nodes);
        }
    }

    T operate(T a, T b) {
        return a + b;
    }

    void pull() {
        val = operate(leftChild->val, rightChild->val);
    }

    void push() {
        extend();

        if(lazy) {
            val = right-left+1;

            if(left != right) {
                leftChild->lazy = 1;
                rightChild->lazy = 1;
            }

            lazy = 0;
        }
    }

    T query(int qs, int qe) {
        push();

        if(right < qs or left > qe) return noOverlapVal;

        if(left >= qs and right <= qe) return val;

        return operate(leftChild->query(qs, qe), rightChild->query(qs, qe));
    }

    T query(int i) {
        return query(i, i);
    }

    void update(int qs, int qe, T upd) {
        push();

        if(right < qs or left > qe) return;

        if(left >= qs and right <= qe) {
            lazy = 1;
            push();
            return;
        }

        leftChild->update(qs, qe, upd);
        rightChild->update(qs, qe, upd);

        pull();
    }
};

SegTreeNode<int> nodes[(int)3e6];


class CountIntervals {
public:
    SegTreeNode<int>* root;

    CountIntervals() {
        i = 0;
        root = &(*(nodes+(i++)));
        root->init(0, 1e9, nodes);
    }

    void add(int left, int right) {
        root->update(left, right, 1);
    }

    int count() {
        return root->query(0, 1e9);
    }
};
