/**
 * let `n` is the number of the unoverlapping intervals of the data structure `CountIntervals`
 *
 * Space Complexity: O(n)
 */
class CountIntervals {
 public:
  /**
   * Time Complexity: O(1)
   * Space Complexity: O(1)
   */
  CountIntervals() : count_(0) {
  }

  /**
   * Time Complexity [Average]: O(log(n))
   * Space Complexity: O(1)
   */
  void add(const int left, const int right) {
    int start = left;
    int end = right + 1;
    auto lb = intervals_.lower_bound(start);
    auto prev_lb = lb == intervals_.begin() ? intervals_.end() : prev(lb);
    for (auto itr = lb; itr != intervals_.end() && itr->first <= end; itr = intervals_.erase(itr)) {
      end = max(itr->second, end);
      count_ -= itr->second - itr->first;
    }
    if (prev_lb != intervals_.end() && prev_lb->second >= left) {
      end = max(prev_lb->second, end);
      start = prev_lb->first;
      count_ -= prev_lb->second - prev_lb->first;
      intervals_.erase(prev_lb);
    }
    count_ += end - start;
    intervals_[start] = end;
  }

  /**
   * Time Complexity: O(1)
   * Space Complexity: O(1)
   */
  int count() {
    return count_;
  }

 private:
  map<int, int> intervals_;
  int count_;
};
