class CountIntervals {
public:
    int k=0;
    set<pair<int,int>>s;
    CountIntervals() {
        k=0;
        s.clear();
    }

    void add(int l, int r) {

        auto it=s.upper_bound({l,r});

        auto p=it;
        if(p!=s.begin())
        p--;

        if(it!=s.begin())
        {
            if(p->second>=l)
            {
                --it;
            }
        }

        while(true)
        {
            if(it==s.end())
                break;
            if(it->first>r)
                break;

            k-=(it->second-it->first+1);

            l=min(l,it->first);
            r=max(r,it->second);
            it=s.erase(it);

            if(it==s.end())
                break;
        }

        k+=(r-l+1);
        s.insert({l,r});
    }

    int count() {
        return k;
    }
};

