class CountIntervals {
    TreeMap<Integer,Integer> tm;
    int count;
    public CountIntervals() {
        tm = new TreeMap<>();
        count =0;
    }

    public void add(int left, int right) {

        if(tm.floorKey(left)!=null) {
            int floorKey1 = tm.floorKey(left);
            if(tm.get(floorKey1)>=left){
                left = floorKey1;
            }
        }
        if(tm.floorKey(right)!=null) {
            int floorKey2 = tm.floorKey(right);
            if(tm.get(floorKey2)>right) {
                right = tm.get(floorKey2);
            }     
        }

        int countToRemove = 0;
        for(int key: tm.subMap(left,right+1).keySet()) {
            countToRemove+=tm.get(key)-key+1;
        }
        count-=countToRemove;

        // Clear all inbetween values
        tm.subMap(left,right+1).clear();

        tm.put(left,right);
        count+=right-left+1;
    }

    public int count() {
        return count;
    }
}

