class CountIntervals {

    public class Interval {

        private int left;
        private int right;

        public Interval(int left, int right) {
            this.left = left;
            this.right = right;
        }

        public int size() {
            return this.right - this.left + 1;
        }

        public int compareTo(Interval other) {
            /* This and other intervals can be merged */ 
            if (isInside(other) || isOutside(other) || isRightColliding(other) || 
            isLeftColliding(other)) {
                return 0;
            /* If other is to the right of this, return negative number */
            } else if (this.left < other.left) {
                return this.right - other.left + 1;
            /* If other is to the left of this, return positive number */
            } else {
                return this.left - other.right - 1;
            }
        }

        public boolean mergeInto(Interval other) {
            if (isInside(other)) return true;
            if (isOutside(other)) {
                this.left = other.left;
                this.right = other.right;
            } else if (isRightColliding(other)) {
                this.right = other.right;
            } else if (isLeftColliding(other)) {
                this.left = other.left;
            } else {
                return false;
            }
            return true;
        }

        public String toString() {
            return "[" + this.left + "," + this.right + "]";
        }

        private boolean isInside(Interval other) {
            return other.left >= this.left && other.right <= this.right;
        }

        private boolean isOutside(Interval other) {
            return this.left >= other.left && this.right <= other.right; 
        }

        private boolean isRightColliding(Interval other) {
            return this.right >= other.left - 1 && this.right <= other.right;
        }

        private boolean isLeftColliding(Interval other) {
            return other.right >= this.left - 1 && other.right <= this.right;
        }

    }

    public class BooleanInteger {
        public boolean booleanVal;
        public int intVal;

        public BooleanInteger(boolean b, int i) {
            this.booleanVal = b;
            this.intVal = i;
        }

        public String toString() {
            return "(" + booleanVal + "," + intVal + ")";
        }

    }

    private List<Interval> intervals;
    private int count;

    public CountIntervals() {
        this.intervals = new ArrayList<>();
        this.count = 0;
    }

    public void add(int left, int right) {
        Interval interval = new Interval(left, right);
        BooleanInteger place = findInsertionPlace(interval);
        /* Merge intervals which can be merged into interval, and 
        delete them. */
        if (place.booleanVal) {
            int end = place.intVal - 1;
            for (int i = place.intVal; i < this.intervals.size(); i++) {
                Interval curr = this.intervals.get(i);
                if (!interval.mergeInto(curr)) break;
                end++;
                this.count -= curr.size(); 
            }
            this.intervals.subList(place.intVal, end + 1).clear();
        }
        this.intervals.add(place.intVal, interval);
        this.count += interval.size();
    }

    public int count() {
        return this.count;
    }

    private BooleanInteger findInsertionPlace(Interval interval) {
        if (this.intervals.isEmpty()) return new BooleanInteger(false, 0);
        return bFindInsertionPlace(interval);
    }

    private BooleanInteger bFindInsertionPlace(Interval interval) {
        int start = 0;
        int end = this.intervals.size() - 1;
        while (start <= end) {
            int mid = (start + end) / 2;
            Interval midInterval = this.intervals.get(mid);
            /* If interval is to the right of midInterval */
            if (interval.compareTo(midInterval) > 0) {
                start = mid + 1;
            /* Otherwise search left */
            } else {
                end = mid - 1;
            }
        }
        BooleanInteger result = new BooleanInteger(false, start);
        if (start >= 0 && start < this.intervals.size()) {
            /* If the interval ar start index can be merged into this interval,
            return true to signal that there are some intervals that need to be
            deleted. */
            result.booleanVal = interval.compareTo(this.intervals.get(start)) == 0;
        }
        return result;
    } 

}

