class CountIntervals {

    TreeMap<Integer, Integer> treeMap;
        int count;
        public CountIntervals() {
            treeMap = new TreeMap<>();
            count = 0;
        }

        public void add(int left, int right) {
            int temp = 0;
            var it = treeMap.floorEntry(left);
            if(it == null || (it.getValue() < left))
                it = treeMap.ceilingEntry(left);
            while (it != null && it.getKey() <= right){
                left = Math.min(left, it.getKey());
                right = Math.max(right, it.getValue());
                temp += (it.getValue() - it.getKey()+1);
                treeMap.remove(it.getKey());
                it = treeMap.ceilingEntry(left);
            }
            treeMap.put(left, right);
            System.out.println(left + "   " + right);
            count += (right-left+1);
            count -= temp;
        }

        public int count() {
            return count;
        }
}
