		class LinkedListRep {
    constructor(public front: LinkedList, public tail: LinkedList, public liArr : LinkedList[]) {}
    }
    class LinkedList {
    public setRep: LinkedListRep;
    constructor(
      public key = 0,
      public color = "B",
      public left: LinkedList | null = null,
      public right: LinkedList | null = null,
      public p: LinkedList | null = null
    ) {
      this.setRep = new LinkedListRep(this, this, [this]);
    }
  }

  class BST {
    constructor(private root: LinkedList | null = null) {}

    private rotateLeft = function (x: LinkedList) {
      let y = x.right!;
      y.p = x.p;

      if (x.p === null) this.root = y;
      else if (x.p.left === x) x.p.left = y;
      else x.p.right = y;

      x.right = y.left;
      if (x.right !== null) x.right.p = x;

      y.left = x;
      x.p = y;
    };
    private rotateRight = function (y: LinkedList) {
      let x = y.left!;
      x.p = y.p;

      if (y.p === null) this.root = x;
      else if (y.p.left === y) y.p.left = x;
      else y.p.right = x;

      y.left = x.right;
      if (y.left !== null) y.left.p = y;

      x.right = y;
      y.p = x;
    };
    private insertFixUp = function (x: LinkedList) {
      while (x.p !== null && x.p.color === "R") {
        if (x.p === x.p.p!.left) {
          let y = x.p.p!.right;
          if (y !== null && y.color === "R") {
            x.p.color = "B";
            y.color = "B";
            x.p.p!.color = "R";
            x = x.p.p!;
          } else {
            if (x === x.p.right) {
              x = x.p;
              this.rotateLeft(x);
            }

            x.p!.color = "B";
            x.p!.p!.color = "R";
            this.rotateRight(x.p!.p!);
          }
        } else {
          let y = x.p.p!.left;
          if (y !== null && y.color === "R") {
            x.p.color = "B";
            y.color = "B";
            x.p.p!.color = "R";
            x = x.p.p!;
          } else {
            if (x === x.p.left) {
              x = x.p;
              this.rotateRight(x);
            }

            x.p!.color = "B";
            x.p!.p!.color = "R";
            this.rotateLeft(x.p!.p!);
          }
        }
      }
      this.root.color = "B";
    };

    insertNode = function (key: number) {
      let t = this.root;
      let y = t;
      let node = new LinkedList(key, "R");
      while (t !== null) {
        y = t;
        if (key < t.key) t = t.left;
        else t = t.right;
      }

      if (y === null) this.root = y = node;
      else {
        t = node;
        if (key < y.key) y.left = t;
        else y.right = t;
        t.p = y;
      }
      this.insertFixUp(node);
    };

    searchImmMinNode = function (key: number): LinkedList | null {
      let t = this.root;
      let immediateSmaller: LinkedList | null = null;
      while (t !== null) {
        if (key === t.key) return t;
        else if (key < t.key) t = t.left;
        else {
          immediateSmaller = t;
          t = t.right;
        }
      }

      return immediateSmaller;
    };

    searchImmMaxNode = function (key: number): LinkedList | null {
      let t = this.root;
      let immediateGreater: LinkedList | null = null;
      while (t !== null) {
        if (key === t.key) return t;
        else if (key < t.key) {
          immediateGreater = t;
          t = t.left;
        } else t = t.right;
      }

      return immediateGreater;
    };
    }

    class CountIntervals {
    private Lbst: BST;
    private Rbst: BST;
    private range: number;
    constructor() {
      this.Lbst = new BST();
      this.Rbst = new BST();
      this.range = 0;
    }

    add(left: number, right: number): void {


      const immMax = this.Rbst.searchImmMaxNode(left);
      const immMin = this.Lbst.searchImmMinNode(right);
      if(!immMax || !immMin || (left < immMax.setRep.front.key && right < immMax.setRep.front.key)) {
        this.Lbst.insertNode(left);
        this.Rbst.insertNode(right);
        const rightNode = this.Rbst.searchImmMinNode(right)!;
        const leftNode = this.Lbst.searchImmMaxNode(left)!;
        leftNode.setRep.tail = rightNode.setRep.tail;
        rightNode.setRep = leftNode.setRep;
        leftNode.setRep.liArr.push(rightNode);
        this.range += (right - left + 1);
        return;
      }

      if(left >= immMax.setRep.front.key && right <= immMax.setRep.tail.key) return;
      if (left < immMax.setRep.front.key) this.Lbst.insertNode(left);
      if (right > immMin.setRep.tail.key) {
        this.Rbst.insertNode(right);
        this.range++;
      }

      const refNode = this.Lbst.searchImmMinNode(left)!;
      let node = this.Rbst.searchImmMaxNode(left);
      while (!!node && node.setRep.front.key <= right) {
        this.range -= node.setRep.tail.key - node.setRep.front.key + 1;
        if (node.setRep.liArr.length > refNode.setRep.liArr.length) {
          let l1 = refNode.setRep.liArr;
          node.setRep.front = refNode.setRep.front;
          for (let i = 0; i < l1.length; i++) {
            l1[i].setRep = node.setRep;
            node.setRep.liArr.push(l1[i]);
          }
        } else if(node.setRep !== refNode.setRep) {
          let l1 = node.setRep.liArr;
          refNode.setRep.tail = node.setRep.tail;
          for (let i = 0; i < l1.length; i++) {
            l1[i].setRep = refNode.setRep;
            refNode.setRep.liArr.push(l1[i]);
          }
        }
        node = this.Rbst.searchImmMaxNode(node.setRep.tail.key + 1);
      }
      this.range += (refNode.setRep.tail.key - refNode.setRep.front.key + 1);

    }

    count(): number {
      return this.range;
    }
    }
