        public void Add(int left, int right)
        {

            if(left>max || right<min || isFull)
                return;
            if(max==min){
                v=1;
                return;
            }

            if(left<=min && max<=right){
                v=max-min+1;
                isFull=true;
                return;
            }
            GetLeft().Add(left,right);
            GetRight().Add(left,right);

            v=GetLeft().v+GetRight().v;

        }



        Node GetLeft()
        {
            if(_left==null)
                _left=new Node(min,(max+min)/2);
            return _left;

        }
        Node GetRight()
        {
            if(_right==null)
                _right=new Node((max+min)/2+1,max);
            return _right;

        }

    }

