# Code
```
class CountIntervals {
public:
    typedef long long int64;
    map<int, int> mp;
    int prevAddedCount;
    int currAddedCount;
    int prevCountAns;

    CountIntervals() {
        prevAddedCount = 0;
        currAddedCount = 0;
        prevCountAns = -1;
    }

    void deleteRange(int left, int right) {
        if(mp.empty()) {
            return;
        }
        auto it = mp.lower_bound(left); // left <= it->first
        if(it != mp.begin()) {
            it--; // it->first < left
        }
        vector<int> v;
        while(it != mp.end() && it->first <= right) {
            if(it->first < left && it->second >= left) {
                int temp = it->second;
                it->second = left - 1;
                if(temp > right) {
                    mp[right + 1] = temp;
                }
            } else if(it->first >= left) {
                if(it->second > right) {
                    mp[right + 1] = it->second;
                }
                v.push_back(it->first);
            }
            it++;
        }
        for(int i : v) mp.erase(i);
    }

    void add(int left, int right) {
        currAddedCount++;
        if(mp.count(left) != 0) {
            if(mp[left] == right) {
                return;
            }
        }
        deleteRange(left, right);
        mp[left] = right;
        auto it = mp.find(left);
        if(it != mp.begin() && prev(it)->second + 1 == left) {
            it--;
            it->second = right;
            mp.erase(left);
        }
        if(it != prev(mp.end()) && next(it)->first == right + 1) {
            it->second = next(it)->second;
            mp.erase(next(it));
        }
    }

    int count() {
        if(prevCountAns != -1 && prevAddedCount == currAddedCount) {
            return prevCountAns;
        }
        // cout << "calling count" << endl;
        int64 sum = 0;
        for(pair<int, int> p : mp) {
            int left = p.first;
            int right = p.second;
            // cout << left << " , " << right << endl;
            sum += (right - left + 1);
        }
        prevAddedCount = currAddedCount;
        return prevCountAns = sum;
    }
};

