from sortedcontainers import SortedList

class CountIntervals:

    def __init__(self):
        self.sl = SortedList()
        self.total = 0
        self.inf = 10**10

    def add(self, left: int, right: int) -> None:
        leftIdx = self.sl.bisect_right((left,self.inf)) - 1
        remove = []
        leftMost,rightMost = left,right

        if leftIdx>=0:
            L,R = self.sl[leftIdx]
            if L<=left<=right<=R:
                return

            if L<=left<=R<=right:
                leftMost = L
                remove.append((L,R))

        leftIdx+=1
        while leftIdx<len(self.sl):
            L,R = self.sl[leftIdx]

            if left<=L<=R<=right:
                remove.append((L,R))
                leftIdx+=1
            else:
                break

        if leftIdx<len(self.sl):
            L,R = self.sl[leftIdx]
            if left<=L<=right<=R:
                rightMost = R
                remove.append((L,R))

        for L,R in remove:
            self.total-= R-L+1
            self.sl.remove((L,R))

        self.sl.add((leftMost,rightMost))
        self.total+=rightMost-leftMost+1

    def count(self) -> int:
        return self.total

