            int l = map.floorKey(right);
            int r = map.get(l);
            count -= (r - l + 1);
            left = Math.min(left, l);
            right = Math.max(right, r);
            map.remove(l);
        }
        count += (right - left + 1);
        map.put(left, right);
    }

    public int count() {
        return count;
    }
