		BST* right_most = NULL;
		int counter = 0;
		bool flag = true;

	public:
		CountIntervals() {}

		void update(BST* node, int start, int end)
		{
			while (true)
			{
				if (node->start >= end)
					if (node->left)
					{   
						node = node->left;
						continue;
					}
					else if (node->start == end)
					{    
						node->start = start;
						counter += end - start;
						return;
					}
					else
					{
						node->left = new BST(start, end);
						if (node == left_most) left_most = node->left;
						counter += end - start;
						return;
					}

				if (node->end <= start)
					if (node->right)
					{    
						node = node->right;
						continue;
					}
					else if (node->end == start)
					{   
						node->end = end;
						counter += end - start;
						return;
					}
					else
					{   
						node->right = new BST(start, end);
						if (node == right_most) right_most = node->right;
						counter += end - start;
						return;
					}

				break;
			}  

			if (start < node->start)
				if (node->left)
					update(node->left, start, node->start);
				else
				{
					counter += node->start - start;
					node->start = start;
				}

			if (node->end < end)
				if (node->right)
					update(node->right, node->end, end);
				else
				{    
					counter += end - node->end;
					node->end = end;
				}

			return;
		}


		void add(int left, int right) 
		{
			right += 1;

			if (flag)
			{    
				flag = false;
				bst = new BST(left, right);
				left_most = right_most = bst;
				counter = right - left;
				return;
			}

			BST* node;
			if (right <= left_most->start) 
				node = left_most;
			else if (right_most->end <= left) 
				node = right_most;
			else 
				node = bst;

			update(node, left, right);

			return;
		}

		int count() 
		{
			return counter;
		}
	};

