        for(int i=1;i<=n;i++) presum[i]=( presum[i-1]+pre[i] ) % mod ;
        for(int i=n;i>0;i--) suf[i]=suf[i+1]+stn[i];
        for(int i=n;i>0;i--) sufsum[i]=sufsum[i+1]+suf[i];
        vector<int> lftmn(n+1, 0), ritmn(n+1, n+1), aha;

        for(int i=1;i<=n;i++)
        {
            while( aha.size() && stn[aha.back()]>=stn[i] )
            {
                ritmn[ aha.back() ]=i;
                aha.pop_back();
            }
            aha.push_back( i );
        }

        aha.clear();

        for(int i=n;i>0;i--)
        {
            while( aha.size() && stn[ aha.back() ] > stn[i] )
            {
                lftmn[ aha.back() ]=i;
                aha.pop_back();
            }
            aha.push_back( i );
        }

        for(int i=1;i<=n;i++)
        {
            // cout<<lftmn[i]<<S<<ritmn[i]<<D;
            // cout<<presum[i]<<S<<sufsum[i]<<D;
        }

        ll yo=mod*1000000;

        for(int i=1;i<=n;i++)
        {
            ll rit = (presum[ ritmn[i]-1 ]-presum[i] + yo ) % mod;
            rit = (rit-(ritmn[i]-i-1)*pre[i] + yo )%mod ;
            rit = ( rit*( i-lftmn[i] ) )%mod;
            ll lft = ( sufsum[ lftmn[i]+1 ] - sufsum[i] + yo ) %mod;
            lft = ( lft - ( i-lftmn[i]-1 )*suf[i] + yo ) %mod;
            lft=( lft*( ritmn[i]-i ) )%mod;
            //cout<<lft<<S<<rit<<S;
            ll mid =( ( ritmn[i]-i ) * ( i-lftmn[i] ) ) % mod;
            mid=( mid * stn[i] )%mod;
            ans=( ans + (lft+rit+mid)*stn[i] )%mod;
            //cout<<ans<<D;
        }

        return ans;
    }
};

