    public void add(int left, int right) 
    {
        Integer l = st.floorKey(left);
        if(l != null)
        {
            if(st.get(l) >= left)
            {
                left = l;
                right = Math.max(st.get(l), right);
                res -= st.get(l) - l + 1;
                st.remove(l);
            }
        }
        Integer r = st.ceilingKey(left);
        while(r != null && right >= r)
        {
            res -= st.get(r) - r + 1;
            right = Math.max(right, st.get(r));
            st.remove(r);
            r = st.ceilingKey(left);
        }
        st.put(left,right);
        res += right - left + 1;
    }

    public int count() 
