class CountIntervals {
public:
    // set of disjoint intervals

    struct Interval {
        int left;
        int right;

        int size() { return right - left + 1; };

        bool _overlaps(const Interval& other) {
            if (other.left < left) {
                return left <= other.right;
            } else {
                return other.left <= right;
            }
        }

        bool overlaps(const Interval& other) {
            auto result = _overlaps(other);
            return result;
        }
    };

    std::map<int, Interval> begin;
    int _count;

    CountIntervals() : begin(), _count(0) {

    }

    void add(int left, int right) {
        Interval i = { left, right};

        auto begin_it = begin.lower_bound(left);

        if (begin_it == begin.begin()) {
            if (begin_it != begin.end() && begin_it->second.left == left) {
                this->_count -= begin_it->second.size();
                begin_it->second.right = std::max(begin_it->second.right, right);
                this->_count += begin_it->second.size();
            } else {
                begin.emplace(left, i);
                this->_count += i.size();
            }
            begin_it = begin.begin();
        } else {
            if (begin_it->second.left != left) {
                begin_it--;
            }

            if (begin_it->second.overlaps(i)) {
                this->_count -= begin_it->second.size();
                begin_it->second.right = std::max(right, begin_it->second.right);
                this->_count += begin_it->second.size();
            } else {
                begin.emplace(left, i);
                this->_count += i.size();
            }
        }

        auto next_it = std::next(begin_it);
        if (next_it != begin.end() && !next_it->second.overlaps(begin_it->second)) {
            begin_it++;
            next_it++;
        }

        if (begin_it != begin.end()) {
            auto coalesce_it = std::next(begin_it);
            while (coalesce_it != begin.end() && coalesce_it->second.overlaps(begin_it->second)) {
