class CountIntervals {

    // TreeMap to store intervals {start : end}
    private TreeMap<Integer, Integer> treeMap;
    private int count;

    public CountIntervals() {
        treeMap = new TreeMap<>();
        count = 0;
    }
    // Time complexity: n*log(n), n - no of merged intervals
    public void add(int left, int right) {
        int start = left;
        int end = right;
        // Current count
        int currentCount = end - start;

        Map.Entry<Integer, Integer> floor = treeMap.floorEntry(left);
        if (floor != null) {
            if (floor.getValue() >= end) return;
            if (floor.getValue() >= start) {
                // Updating the current count as interval overlap
                // TreeMap: [6, 10] and current: [8, 15]
                currentCount -= floor.getValue() - start;
                treeMap.remove(floor.getKey());
                start = floor.getKey();
            }
        }

        Map.Entry<Integer, Integer> ceiling = treeMap.ceilingEntry(left);
        // Need a loop here. Example: TreeMap: [6,8], [10,15], [18,22] and current: [5,20]
        while (ceiling != null && ceiling.getKey() <= end) {
            treeMap.remove(ceiling.getKey());
            currentCount -= Math.min(ceiling.getValue(), end) - ceiling.getKey();
            end = Math.max(ceiling.getValue(), end);
            ceiling = treeMap.ceilingEntry(left);
        }

        count += currentCount;
        treeMap.put(start, end);
    }

	// Time complexity: O(1)
    public int count() {
        // Adding the size of the map as need to include interval bounderies
        return count + treeMap.size();
    }
}
