class CountIntervals {

    TreeMap<Integer, Integer> map = new TreeMap<>();
    int count = 0;
    public CountIntervals() {

    }

    public void add(int left, int right) {
        Integer l = map.floorKey(left);
        Integer r = map.floorKey(right);
        left = l == null || map.get(l) < left? left : l; // find the left bound (min)
        right = Math.max(right, r == null? 0 : map.get(r)); // find the right bound (max)
        Map<Integer, Integer> dup = map.subMap(left, true, right, true); // use submap
        for (int key : dup.keySet()){ // delete ALL that's in the submap
            count -= (map.get(key)-key+1);
        }
        dup.clear(); // delete the map
        map.put(left, right); // THEN, we put it in.
        count += (right - left + 1);
    }

    public int count() {
        return count;
    }
}
