public class CountIntervals {
    private int count;
    private final TreeMap<Integer, Integer> intervals;

    public CountIntervals() {
        this.intervals = new TreeMap<>();
    }

    public void add(int left, int right) {
        while (true) {
            Integer floorKey = this.intervals.floorKey(right);
            int floorVal = floorKey == null ? 0 : this.intervals.get(floorKey);
            if (floorKey == null || floorVal < left) break; // no more intersections left
            left = Math.min(floorKey, left);
            right = Math.max(floorVal, right);
            this.intervals.remove(floorKey);
            this.count -= floorVal - floorKey + 1;
        }
        this.intervals.put(left, right);
        this.count += right - left + 1;
    }

    public int count() {
        return this.count;
    }
}
