class CountIntervals {
    List<int[]> intervals;
    int total = 0;
    public CountIntervals() {
        intervals = new ArrayList();
    }

    public void add(int left, int right) {
        int index = binarySearch(left, right);
        int preSum = 0;
        int newSum = 0;
        if(index-1>=0 && intervals.get(index-1)[1]>=left) {
            index-=1;
            left = intervals.get(index)[0];
            right = Math.max(right, intervals.get(index)[1]);
            preSum+=(intervals.get(index)[1] - intervals.get(index)[0]+1);
            intervals.remove(index);
        }
        while(index<intervals.size() && right>=intervals.get(index)[0]) {
            preSum+=(intervals.get(index)[1] - intervals.get(index)[0]+1);
            right = Math.max(right, intervals.get(index)[1]);
            intervals.remove(index);
        }
        newSum = right-left+1;
        intervals.add(index, new int[]{left, right});
        total+=(newSum-preSum);
    }

    public int count() {
        return total;
    }

    private int binarySearch(int left, int right) {
        int start = 0;
        int end = intervals.size();
        while(start<end) {
            int mid = (start+end)/2;
            if(intervals.get(mid)[0]<left) {
                start = mid+1;
            }else if (intervals.get(mid)[0]>left) {
                end = mid;
            } else {
                return mid;
            }
        }
        return start;
    }
}
