class CountIntervals {
public:
    int cnt;
    map<int,int> intervals;
    CountIntervals() {
        cnt = 0;
    }

    void add(int left, int right) 
    {

        int newIntervalFirst = left;
        int newIntervalSecond = right;

        auto it = intervals.upper_bound(right);

        // the size of the map is zero or the size of the start of the first element is greater than the right;
        // in that case we simply have to insert the element;

        if(intervals.size() == 0 || it == intervals.begin())
        {
            intervals[left] = right;
            cnt = cnt + (right - left) + 1;
            return;
        }

        it--;

        // condition for overlapping;
        while((it->second) >= newIntervalFirst)
        {
            int firstValue = it->first;
            int secondValue = it->second;

            // this interval get merged with the new interval
            // so we subtract  the element of this from the count , and also erase thet interval from the intervals map;

            intervals.erase(firstValue);
            cnt = cnt - (secondValue - firstValue + 1);

            newIntervalFirst = min(newIntervalFirst , firstValue);
            newIntervalSecond = max(newIntervalSecond , secondValue);

            it = intervals.upper_bound(newIntervalSecond);

            if(intervals.size() == 0 || it == intervals.begin())
            {
                break;
            }
            it--;
        }

        cnt = cnt + (newIntervalSecond - newIntervalFirst) + 1;

        intervals[newIntervalFirst] = newIntervalSecond;
        return;
    }

    int count() {
        return cnt;
    }
};

